package com.lili.mall.api.goods.domain;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.annotation.TableName;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;/** * * @title Brand * @description 商品品牌 * @author  xiehongjun * @date    14:50 2021/4/18 */@Data@NoArgsConstructor@AllArgsConstructor@TableName(value = "brand")public class Brand {    /** 数据主键     *  1、value 属性名和数据库字段名相同时，可不设置 value 值     *  2、IdType.AUTO  设置 id 自增     */    @TableId(value = "id", type = IdType.AUTO)    private Integer     id;    /** 品牌名称*/    private String  name;    /** 品牌图片*/    private String  image;    /** 平台首字母*/    private String  initial;    /** 排序*/    private Integer  sort;}