package com.lili.mall.common.r;import java.io.Serializable;/** * * @title RespResult * @description 请求响应结果 * @author  xiehongjun * @date    14:07 2021/4/18 */public class RespResult<T> implements Serializable {    private static final long serialVersionUID = 6521874415896496954L;    /** 响应数据结果集*/    private T data;    /** 响应状态码*/    private Integer code;    /** 请求应消息*/    private String message;    public T getData() {        return data;    }    public void setData(T data) {        this.data = data;    }    public Integer getCode() {        return code;    }    public void setCode(Integer code) {        this.code = code;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public RespResult() {    }    public RespResult(RespCode respCode) {        this.code = respCode.getCode();        this.message = respCode.getMessage();    }    public RespResult(T data, RespCode respCode) {        this.code = respCode.getCode();        this.message = respCode.getMessage();        this.data = data;    }    public static<T>  RespResult<T> ok(){        return new RespResult<T>(null, RespCode.SUCCESS);    }    public static<T>  RespResult<T> ok(T data){        return new RespResult<T>(data, RespCode.SUCCESS);    }    public static<T>  RespResult<T>  error(){        return new RespResult<T>(null, RespCode.ERROR);    }    public static<T>  RespResult<T>  error(RespCode respCode){        return new RespResult<T>(respCode);    }    public static<T>  RespResult<T>  error(String message){        return RespResult.secByError( RespCode.ERROR.getCode(), message);    }    public static<T>  RespResult<T>  secByError(Integer code, String message) {        RespResult<T>  err = new RespResult<T>();        err.setCode(code);        err.setMessage(message);        return err;    }}